        if(citycode===''||citycode===null){
            Ext.MessageBox.alert('提示','地市不能为空！');
            return;
        }
        if(question===''||question===null){
            Ext.MessageBox.alert('提示','客户问不能为空！');
            return;
        }
        Ext.Ajax.request({
            url: '../wordclasssentence.action',
            method: 'post',
            params: {
                action:'wordpat',
                question: question,
                citycode:citycode
            },
            async: false,
            success: function (result, request) {
                var word = [];
                var retobj = Ext.JSON.decode(result.responseText);
                var resultStr = retobj.result;
                var startWith = function(str,value){
                    var reg=new RegExp("^"+str);
                    return reg.test(value);
                };
                if(retobj.success){
                    var lst = [];
                    var nlst = [];
                    var wordpatres = retobj.wordpatResult;
                    for(var j = 0 ; j < wordpatres.length; j++){
                        var owordpat = wordpatres[j].wordpat;
                        var nwordpat = wordpatres[j].newWordpat;
                        lst.push(owordpat.substring(0,owordpat.indexOf("#无序#")));
                        nlst.push(nwordpat.substring(0,nwordpat.indexOf("#无序#")));
                        if(wordpatres[j].OOVWord.length>0){
                            var oovw = wordpatres[j].OOVWord;
                            for(var m=0 ; m < oovw.length; m++){
                                word.push(oovw[m]);
                            }
                        }
                    }
                    openWin(lst,nlst,word);
                }else{
                    Ext.MessageBox.alert('提示', '分词失败！');
                }

            },
            failure: function (result, request) {
                Ext.MessageBox.alert('系统异常', '请求数据失败！');
            }
        });
